// src/components/WelcomePage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './WelcomePage.css';

const WelcomePage = () => {
    const navigate = useNavigate();
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    const images = [
        '/images/hogwarts_castle.jpg',
        '/images/jurassic_park.jpg',
        '/images/spiderman_ride.jpg'
    ];

    const handleNextImage = () => {
        setCurrentImageIndex((currentImageIndex + 1) % images.length);
    };

    const handleContinue = () => {
        navigate('/ticket-purchase');
    };

    return (
        <div className="welcome-container">
            <h1>Welcome to Universal's Islands of Adventure!</h1>
            <div className="image-gallery">
                <img src={images[currentImageIndex]} alt="Islands of Adventure Attraction" onClick={handleNextImage} />
            </div>
            <button className="continue-button" onClick={handleContinue}>Continue to Ticket Purchase</button>
        </div>
    );
};

export default WelcomePage;
