import React from 'react';
import { useNavigate } from 'react-router-dom';
import './WelcomePage.css';

const WelcomePage = () => {
    const navigate = useNavigate();

    const handleContinue = () => {
        navigate('/ticket-purchase');
    };

    return (
        <div className="welcome-container">
            <h1>Welcome to Universal's Islands of Adventure!</h1>
            <div className="image-gallery">
                <img src="/Images/universals_islands_of_adventure.jpg" alt="Islands of Adventure" />
                <img src="/Images/velocicaster_ride_pic.jpg" alt="Velocicoaster"/>
                <img src="/Images/hogwarts_night.jpg" alt="Hogwarts at night"/>
                <img src="/Images/ioa_night.jpg" alt="Islands at night"/>
            </div>
            <button className="continue-button" onClick={handleContinue}>Continue to Ticket Purchase</button>
        </div>
    );
};

export default WelcomePage;
